(function() {
    'use strict';

    // Загрузка jQuery если его нет
    function loadJQuery(callback) {
        if (typeof jQuery === 'undefined') {
            var script = document.createElement('script');
            script.src = 'https://cdnjs.cloudflare.com/ajax/libs/jquery/3.7.1/jquery.min.js';
            script.onload = function() {
                callback(jQuery);
            };
            document.head.appendChild(script);
        } else {
            callback(jQuery);
        }
    }

    function initSnowEffect($) {
        let activeSnowflakes = 0;
        const MAX_SNOWFLAKES = 5;
        const defaultSettings = {
            character: "❄",
            speed: 50000,
            frequency: 12000,
            color: "#0CB5ED",
            blur: false,
            enabled: true,
            small: 2,
            large: 4,
            wind: 2,
            windVariance: 1,
            startOpacity: 0.2,
            endOpacity: 0,
            overflow: "hidden",
            zIndex: 9999
        };

        window.SnowEffect = {
            settings: defaultSettings,
            
            init: async function(scriptUrl) {
                try {
                    const response = await fetch(scriptUrl);
                    const data = await response.json();
                    Object.assign(this.settings, data);
                    if (this.settings.enabled) {
                        $('.snow-container').remove();
                        this.start();
                    } else {
                        this.stop();
                    }
                } catch (error) {
                    console.warn('Failed to load settings:', error);
                }
            },

            start: function() {
                const $container = $('<div>')
                    .addClass('snow-container')
                    .css({
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        pointerEvents: 'none',
                        overflow: this.settings.overflow,
                        zIndex: this.settings.zIndex
                    })
                    .appendTo('body');

                const createFlake = () => {
                    if (!this.settings.enabled || activeSnowflakes >= MAX_SNOWFLAKES) return;

                    activeSnowflakes++;

                    const fontSize = Math.random() * (this.settings.large - this.settings.small) + this.settings.small;
                    const startX = Math.random() * window.innerWidth;
                    const endX = startX + Math.random() * this.settings.wind * 2 - this.settings.wind;
                    const endY = window.innerHeight;
                    const rotation = Math.random() * this.settings.windVariance * 2 - this.settings.windVariance;
                    const speed = this.settings.speed * (0.8 + Math.random() * 0.4);

                    const $flake = $('<span>')
                        .addClass('snowflake')
                        .html(this.settings.character)
                        .css({
                            position: 'absolute',
                            top: -fontSize,
                            left: startX,
                            color: this.settings.color,
                            fontSize: fontSize + 'px',
                            opacity: this.settings.startOpacity,
                            transition: `transform ${speed/1000}s linear, opacity ${speed/1000}s ${this.settings.opacityEasing}`,
                            transform: 'rotate(0deg)'
                        })
                        .appendTo($container);

                    setTimeout(() => {
                        if (!this.settings.enabled) {
                            activeSnowflakes--;
                            $flake.remove();
                            return;
                        }
                        $flake.css({
                            transform: `translate(${endX - startX}px, ${endY}px) rotate(${rotation}deg)`,
                            opacity: this.settings.endOpacity
                        });
                    }, 50);

                    setTimeout(() => {
                        if ($flake.parent().length) {
                            activeSnowflakes--;
                            $flake.remove();
                        }
                    }, speed + 1000);
                };

                const generateFlakes = () => {
                    if (this.settings.enabled && activeSnowflakes < MAX_SNOWFLAKES) {
                        createFlake();
                    }
                    if (this.settings.enabled) {
                        setTimeout(generateFlakes, this.settings.frequency);
                    }
                };

                generateFlakes();
            },

            stop: function() {
                $('.snow-container').remove();
                activeSnowflakes = 0;
            }
        };
    }

    // Инициализация
    loadJQuery(function($) {
        initSnowEffect($);
        
        // Инициализация с настройками из Google Таблицы
        window.SnowEffect.init('https://script.google.com/macros/s/AKfycbxXDc1zV88D0nJxYtc3KoPn6WTiG9qIo2zlo9RSccHi7cpEMMFireralBh3q6p-5uVDLA/exec');
        
        // Проверка обновлений каждые 5 секунд
        setInterval(function() {
            if (document.visibilityState === 'visible') {
                window.SnowEffect.init('https://script.google.com/macros/s/AKfycbxXDc1zV88D0nJxYtc3KoPn6WTiG9qIo2zlo9RSccHi7cpEMMFireralBh3q6p-5uVDLA/exec');
            }
        }, 5000);
    });
})();