/*!
 * Snow Effect by Selezen v1.0.6
 */
(function() {
    'use strict';

    // Сначала загружаем jQuery если его нет
    function loadJQuery(callback) {
        if (window.jQuery) {
            callback(window.jQuery);
        } else {
            var script = document.createElement('script');
            script.src = 'https://cdnjs.cloudflare.com/ajax/libs/jquery/3.7.1/jquery.min.js';
            script.onload = function() {
                callback(window.jQuery);
            };
            document.head.appendChild(script);
        }
    }

    // Основная функция снежного эффекта
    function initSnowEffect($) {
        var isRunning = false;
        let activeSnowflakes = 0;
        const MAX_SNOWFLAKES = 100;

        const defaultSettings = {
            character: "❄",
            speed: 40000,
            frequency: 500,
            color: "#Fa0000",
            blur: false,
            enabled: true,
            small: 16,
            large: 32,
            wind: 100,
            windVariance: 50,
            startOpacity: 1,
            endOpacity: 0,
            overflow: "hidden",
            zIndex: 9999
        };

        window.SnowEffect = {
            settings: {...defaultSettings},
            
            init: async function(scriptUrl) {
                try {
                    if (scriptUrl) {
                        const response = await fetch(scriptUrl);
                        const data = await response.json();
                        
                        // Применяем только валидные настройки
                        Object.keys(defaultSettings).forEach(key => {
                            if (data[key] !== undefined && data[key] !== null) {
                                this.settings[key] = data[key];
                            }
                        });
                    }
                } catch (error) {
                    console.warn('Використовуються налаштування за замовчуванням:', error);
                }

                if (this.settings.enabled) {
                    if (!isRunning) this.start();
                } else if (!this.settings.enabled && isRunning) {
                    this.stop();
                }
            },

            start: function() {
                if (isRunning) this.stop();
                isRunning = true;
                activeSnowflakes = 0;

                const $container = $('<div>')
                    .addClass('snow-container')
                    .css({
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        pointerEvents: 'none',
                        overflow: this.settings.overflow,
                        zIndex: this.settings.zIndex
                    })
                    .appendTo('body');

                const createFlake = () => {
                    if (!isRunning || activeSnowflakes >= MAX_SNOWFLAKES) return;

                    activeSnowflakes++;
                    const fontSize = Math.random() * (this.settings.large - this.settings.small) + this.settings.small;
                    const startX = Math.random() * window.innerWidth;
                    const windOffset = (Math.random() - 0.5) * this.settings.wind;
                    const endY = window.innerHeight + fontSize;
                    
                    const $flake = $('<span>')
                        .html(this.settings.character)
                        .css({
                            position: 'absolute',
                            top: -fontSize,
                            left: startX,
                            color: this.settings.color,
                            fontSize: fontSize + 'px',
                            opacity: this.settings.startOpacity,
                            textShadow: this.settings.blur ? '0 0 5px rgba(255,255,255,0.8)' : 'none',
                            transition: `all ${this.settings.speed/1000}s linear`
                        })
                        .appendTo($container);

                    requestAnimationFrame(() => {
                        $flake.css({
                            transform: `translate(${windOffset}px, ${endY}px) rotate(360deg)`,
                            opacity: this.settings.endOpacity
                        });
                    });

                    setTimeout(() => {
                        activeSnowflakes--;
                        $flake.remove();
                    }, this.settings.speed);
                };

                const generateFlakes = () => {
                    if (isRunning) {
                        createFlake();
                        setTimeout(generateFlakes, this.settings.frequency);
                    }
                };

                generateFlakes();
            },

            stop: function() {
                isRunning = false;
                $('.snow-container').remove();
                activeSnowflakes = 0;
            }
        };

        // Инициализация с настройками из Google Таблицы
        const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbxXDc1zV88D0nJxYtc3KoPn6WTiG9qIo2zlo9RSccHi7cpEMMFireralBh3q6p-5uVDLA/exec';
        window.SnowEffect.init(GOOGLE_SCRIPT_URL);
    }

    // Запускаем после загрузки jQuery
    loadJQuery(function($) {
        initSnowEffect($);
    });
})();